INSERT INTO exercises (name, difficulty, tips, link) VALUES ('Ball Hamstring Curl', 1, 'Lay on your back with a stability ball underneath your feet. Fully extend your knees and hips simultaneously. Flex your knees while keeping your hips extended. Flex your hamstrings and then return to the starting position. ', 'https://www.youtube.com/watch?v=08topj65M-8%7C');
