INSERT INTO exercises (name, difficulty, tips, link) VALUES ('Dumbbell Single Leg Single Arm Deadlift', 2, 'Start by standing on one leg with a dumbbell in one hand. Slowly bend at the hips and lower the dumbbell towards the ground while extending your free leg behind you. Keep your core engaged and your back straight as you lower the weight. Once the dumbbell is close to the ground, push through your heel to return to the starting position. ', '');
