INSERT INTO exercises (name, difficulty, tips, link) VALUES ('Plate Staggered Deadlift', 2, 'Start by standing with your feet shoulder-width apart and holding a weight plate. Place one foot slightly behind the other, with the heel of your back foot elevated. From this starting position, bend at the hips and knees to lower the plate towards the ground, keeping your back straight and your core engaged. Once the plate is below your knees, drive through your heels to stand back up to the starting position. ', '');
