INSERT INTO exercises (name, difficulty, tips, link) VALUES ('Plate Staggered Pinch Grip Deadlift', 2, 'Start by standing with your feet shoulder-width apart and holding two plates with a pinch grip. Place one foot slightly behind the other, with the heel of your back foot elevated. From this starting position, bend at the hips and knees to lower the plates towards the ground, keeping your back straight and your core engaged. ', '');
