INSERT INTO exercises (name, difficulty, tips, link) VALUES ('Plate Good Morning Anterior', 1, 'Stand with feet hip-width apart, the plate held in front of you, and the knees slightly bent. Bend forward at the hips, keeping the back straight, until the torso is parallel to the floor. Push through the hips to return to the starting position. ', '');
