INSERT INTO exercises (name, difficulty, tips, link) VALUES ('Kettlebell Glute Bridge', 0, 'Laying on the floor with your knees bent and feet firmly on the floor, rest the kettlebell on your pelvis. Pushing through the legs and keeping the core muscles engaged, raise your pelvis off the floor until your stomach, pelvis and thighs make a straight line. Slowly lower to the ground to the starting position and repeat. ', 'https://youtu.be/fWU_4ysp0Y8');
INSERT INTO muscle_groups_in_exercises (exerciseID, muscleID) VALUES (420, 9);
INSERT INTO equipment_in_exercises (exerciseID, equipmentID) VALUES (420, 3);
INSERT INTO exercises (name, difficulty, tips, link) VALUES ('Kettlebell Single Leg Glute Bridge', 1, 'Laying on the floor with one knee bent with the foot firmly on the floor and one leg extended, rest the kettlebell on your pelvis. Pushing through the bent leg and keeping the core muscles engaged, raise your pelvis and the extended leg off the floor until your stomach, pelvis and thighs make a straight line. Slowly lower to the ground to the starting position and repeat. ', 'https://youtu.be/wT8izMVvHi8');
