INSERT INTO exercises (name, difficulty, tips, link) VALUES ('Kettlebell Hip Thrust', 0, 'Sitting on the floor with your knees bent and feet planted firmly on the floor, lean your back against the bench. Resting the kettlebell on your pelvis, raise your pelvis until your stomach, pelvis and thighs are in line. Slowly lower until you are in the starting position and repeat. ', 'https://youtu.be/f3KqAL-Q9Qw');
