INSERT INTO exercises (name, difficulty, tips, link) VALUES ('Glutes Stretch Variation Three', 0, 'Lay flat and bring your left leg to 90 degrees. Hold your left leg with both hands and slowly rotate your hips to the right hand side. Hold at the peak of the stretch, then slowly return to starting position. Switch sides and repeat. ', 'https://youtu.be/10px3OnzfqU');
