INSERT INTO exercises (name, difficulty, tips, link) VALUES ('Glutes Stretch Variation Two', 0, 'Sit on the ground and lay one leg flat and the other over the top. Hold your leg with the same side arm and slowly rotate your hips and back. Hold at the peak of the stretch, then slowly return to starting position. Switch sides and repeat. ', 'https://youtu.be/gr1NjzfIbNk');
INSERT INTO muscle_groups_in_exercises (exerciseID, muscleID) VALUES (425, 9);
