INSERT INTO exercises (name, difficulty, tips, link) VALUES ('Glutes Stretch Variation One', 0, 'On your front, place one leg forward and then stretch out the same side arm across the mat/floor. Fully extend your leg and arm. Hold at the peak of the stretch, then slowly return to starting position. Switch sides and repeat. ', 'https://youtu.be/2th-SvkjF6A');
