INSERT INTO exercises (name, difficulty, tips, link) VALUES ('Cable Hip Abduction', 0, 'Use an ankle attachment. Cable should be set all the way to the bottom. Face sideways with the ankle attachment on your outside leg. Walk a few steps away. Abduct at the hips and raise your leg out to the side. Return to the starting position and stop just short of your foot touching back to the ground (to keep tension on the muscle). ', 'https://youtu.be/ZPmQ-SUD8_g');
INSERT INTO muscle_groups_in_exercises (exerciseID, muscleID) VALUES (427, 9);
