INSERT INTO exercises (name, difficulty, tips, link) VALUES ('Band Glute Kickback', 1, 'Get into a quadruped position. Loop the band around both of your wrists and one of your feet. Extend your knee and hip straight back until you feel like contraction in the glutes. Flex at the knee until back in the starting position. ', 'https://youtu.be/51ujdtKLgIc');
