INSERT INTO exercises (name, difficulty, tips, link) VALUES ('Barbell Behind The Back Wrist Curl', 1, 'Stand straight and hold the barbell behind you using a pronated grip with your hands and feet shoulder-width apart. Slowly curl your wrists in a semi-circular motion upwards. Hold the barbell at the apex of the motion and then slowly lower the barbell back down to starting position. ', 'https://www.youtube.com/watch?v=NzS4PPIhVvY%7C');
