INSERT INTO exercises (name, difficulty, tips, link) VALUES ('Dumbbell Wrist Curl', 0, 'Grip the dumbbell with your palm facing upwards with your forearm rested against the bench. Slowly curl your wrist upwards in a semicircular motion. Return to starting position and repeat. ', 'https://www.youtube.com/watch?v=S-ynXc4M-mY');
