INSERT INTO exercises (name, difficulty, tips, link) VALUES ('Kettlebell Farmers Carry', 0, 'Standing straight with a kettlebell in one hand, lift the knee of one leg off the floor to knee height. Bring the leg back to standing and repeat with the other leg, keeping the kettlebell on the same side. Repeat. ', 'https://youtu.be/w3GqfqD4Sb4');
