INSERT INTO exercises (name, difficulty, tips, link) VALUES ('Kettlebell Wrist Extension', 0, 'Stand up straight with a kettlebell in both hands in front of your pelvis, with your forearms facing inwards. Flex your wrist upwards until wrists are fully extended. Lower to the starting position and repeat. ', 'https://youtu.be/eMv3q7r9n9A');
