INSERT INTO exercises (name, difficulty, tips, link) VALUES ('Kettlebell Wrist Curl', 0, 'Stand up straight with a kettlebell in both hands in front of your pelvis, with your forearms facing outwards. Flex your wrist towards your body until the kettlebell is level with your stomach. Lower to the starting position and repeat. ', 'https://youtu.be/kU3wyBBi-BY');
