INSERT INTO exercises (name, difficulty, tips, link) VALUES ('Dumbbell Plank Pullthrough', 1, 'Get into a plank position. Either from your knees or toes. Start with the DB by your right hand. Reach with your left and pull the dumbbell to your left arm. Then repeat with the right. ', 'https://youtu.be/rdpKCDFZR84');
