INSERT INTO exercises (name, difficulty, tips, link) VALUES ('Barbell Situp', 1, 'Hold the bar with a slightly wider than shoulder width grip. Push the bar up toward the ceiling and maintain that elbow extension. Flex your abs and sit-up. Aim to get your torso vertical while maintaining the barbell overhead. ', 'https://youtu.be/rPy1_8f3vIY');
