INSERT INTO exercises (name, difficulty, tips, link) VALUES ('Long Lever Plank', 2, 'Start in a hand plank position and walk your hands further out until your ears are between your biceps. Hold in this position for the allotted amount of time. Make sure you keep a flat back for the whole set. ', 'https://youtu.be/p1DL0XHQfis');
