INSERT INTO exercises (name, difficulty, tips, link) VALUES ('Hand Plank', 0, 'Start in a kneeling position with your hands planted on the ground. Pick your knees up off the ground. Hold in this position with a flat back. ', 'https://www.youtube.com/watch?v=QMXncCeneZI');
