INSERT INTO exercises (name, difficulty, tips, link) VALUES ('Hollow Hold', 2, 'Lay flat on the ground with your arms extended over head. Pick your upper back and your legs off the ground. Your body should be in a banana shape. Hold in this position. ', 'https://youtu.be/nhkI6pgNaFM');
