INSERT INTO exercises (name, difficulty, tips, link) VALUES ('Dumbbell Hollow Hold', 2, 'Lay flat on the ground with a dumbbell overhead. Pull your upper back and legs off the ground. Make a banana shape with your body. The dumbbell should be held in your hands and your ears should be between your biceps. ', '');
INSERT INTO muscle_groups_in_exercises (exerciseID, muscleID) VALUES (511, 12);
INSERT INTO equipment_in_exercises (exerciseID, equipmentID) VALUES (511, 2);
INSERT INTO exercises (name, difficulty, tips, link) VALUES ('Mountain Climber', 0, 'To perform a bodyweight mountain climber, start in a plank position with your hands placed directly under your shoulders. Bring your right knee towards your chest, keeping your foot close to your hand. Quickly switch legs, bringing your left knee towards your chest. Continue this movement, alternating legs, at a quick pace for a set amount of time or repetitions. ', '');
INSERT INTO muscle_groups_in_exercises (exerciseID, muscleID) VALUES (512, 12);
INSERT INTO equipment_in_exercises (exerciseID, equipmentID) VALUES (512, 9);
INSERT INTO exercises (name, difficulty, tips, link) VALUES ('Plate Situp', 0, 'Start by lying on your back on a mat with your legs bent and feet flat on the ground. Hold a weight plate over your head with both hands, making sure to keep your arms straight. Engage your core and use your abdominal muscles to sit up, keeping the weight plate towards the ceiling. ', '');
