INSERT INTO exercises (name, difficulty, tips, link) VALUES ('Weighted Forearm Plank', 1, 'Get into a kneeling position with both forearms pressed into the ground. Place a weight plate on your back (or have a partner do it for you). Hold the plank for the allotted amount of time. ', '');
