INSERT INTO exercises (name, difficulty, tips, link) VALUES ('Weighted Hand Plank', 1, 'Start in a kneeling position with your hands planted on the ground. Place a weight plate on your back or have a friend do it for you. Pick your knees up off the ground. Hold in this position with a flat back. ', '');
