INSERT INTO exercises (name, difficulty, tips, link) VALUES ('Plate Hollow Hold', 2, 'Lay flat on the ground with your arms extended over head holding a weight plate. Pick your upper back and your legs off the ground. Your body should be in a banana shape. ', '');
