INSERT INTO exercises (name, difficulty, tips, link) VALUES ('Dumbbell Suitcase Crunch', 2, 'Lay flat on the ground with a dumbbell. Point the soles of your shoes at the ceiling as well as the dumbbell. Reach for your feet with the dumbbell before bringing your spine back to the floor. ', '');
