INSERT INTO exercises (name, difficulty, tips, link) VALUES ('TRX Forearm Plank', 0, 'Lie face down on the ground and place your forearms on the ground directly under your shoulders. With your feet in the stirrups, kick both feet into the TRX straps and hold the handles above your head. Tighten your abs, glutes, and legs to form a straight line from your head to your feet. ', '');
