INSERT INTO exercises (name, difficulty, tips, link) VALUES ('TRX Hand Plank', 1, 'Start by facing away from the TRX straps and placing your feet in the TRX handles with your palms on the ground. Lift your knees off the ground to begin the set. Engage your core and maintain this position for the desired amount of time, holding a steady plank. ', '');
