INSERT INTO exercises (name, difficulty, tips, link) VALUES ('TRX Pike', 1, 'Place your feet in the stirrups and get in to a plank position. Pull your hips straight up toward the ceiling. Lower your hips until you''re back in a plank position. ', '');
