INSERT INTO exercises (name, difficulty, tips, link) VALUES ('Kettlebell Russian Twist', 0, 'Sit on the floor with your legs bent, knees together and feet raised slightly off the floor. Starting on one side and sitting up straight with your core muscles engaged, lift the kettlebell off the floor with both hands and bring it across your body to the other side, rotating your chest towards the floor as you do so. Tap the kettlebell lightly on the floor and bring the kettlebell back across your body to the starting position, rotating your chest towards the floor. Repeat, keeping the legs bent and feet raised throughout the exercise. ', 'https://youtu.be/pxIi1wVIrE0');
INSERT INTO muscle_groups_in_exercises (exerciseID, muscleID) VALUES (524, 12);
