INSERT INTO exercises (name, difficulty, tips, link) VALUES ('Kettlebell Windmill', 2, 'Stand with feet slightly wider than shoulder width apart. Hold the kettlebell in one hand and extend your arm above your head. Keeping your legs straight and the kettlebell above your head, lower the relaxed arm towards the ground, twisting your body towards the side of the raised arm. Return to the starting potion, tilt your pelvis forward and repeat. ', 'https://youtu.be/aS3Iutmq6TY');
