INSERT INTO exercises (name, difficulty, tips, link) VALUES ('Side Plank Up Down', 1, 'Start with your elbow and hip on the ground. Either neutral fist or flat hand on the ground. Begin in a side plank position, and then drop your hips to the floor. Pull your hips back to the ceiling to finish the rep. ', 'https://youtu.be/J11b9spQ9r0');
