INSERT INTO exercises (name, difficulty, tips, link) VALUES ('Dumbbell Kneeling Wood Chopper', 0, 'Start in a kneeling position with your shoulder blades pulled back. The dumbbell should begin by one of your hips. Rotate at the spine and bring the dumbbell from your hip, 180 degrees around your body. The dumbbell should land at eye level. ', '');
