INSERT INTO exercises (name, difficulty, tips, link) VALUES ('Dumbbell Overhead Side Bend', 2, 'Start by standing with your feet shoulder-width apart and holding a dumbbell overhead. From this starting position, bend to the side as far as you can while keeping your feet planted and your core engaged. ', '');
