INSERT INTO exercises (name, difficulty, tips, link) VALUES ('Dumbbell Side Plank Up Down', 0, 'Start by lying on your side with your feet stacked on top of each other and a dumbbell on your hip. Place your bottom elbow on the ground beneath your shoulder and engage your core to raise your hips off the ground. Your hips will do all the moving. Think, "hips up, hips down." ', '');
