INSERT INTO exercises (name, difficulty, tips, link) VALUES ('Dumbbell Hand Side Plank', 1, 'Start by lying on your side with your feet stacked on top of each other and a dumbbell on your hip. Place your bottom hand on the ground beneath your shoulder and engage your core to raise your hips off the ground. From this starting position, hold the side plank position for the desired amount of time, making sure to keep your body straight and your hips level. ', '');
