INSERT INTO exercises (name, difficulty, tips, link) VALUES ('Plate Standing Twist', 0, 'Stand with a plate extended away from your body. Elbows mostly extended. Rotate at the upper spine while keeping your lumbar spine in a neutral position. Do not let the plate go behind your body. ', '');
