INSERT INTO exercises (name, difficulty, tips, link) VALUES ('Plate Wood Chopper', 0, 'Stand with a shoulder width stance. Begin with the plate near one of your quads. Rotate at your upper spine to engage the obliques. You can pivot your back foot to keep from generating too much torque at the knee. ', '');
