INSERT INTO exercises (name, difficulty, tips, link) VALUES ('Dumbbell Side Bend', 0, 'Stand with feet hip-width apart and place your right hand on the right hip. Hold a dumbbell with the left hand. Bend at the hips to the side. Make sure you don''t start to lean forward to try and force a longer range of motion. ', '');
