INSERT INTO exercises (name, difficulty, tips, link) VALUES ('TRX Forearm Side Plank', 1, 'Lie on your side with your legs straight and your feet resting in the TRX foot cradles. Prop yourself up on your forearm and engage your core to maintain a straight line from your head to your feet. ', '');
